const raw = $json["content"]
  .replace(/```json/g, "")
  .replace(/```/g, "")
  .trim();

try {
  const parsed = JSON.parse(raw);

  const participants = parsed.participants;
  const activity = parsed.activity;

  if (!Array.isArray(participants)) {
    throw new Error("Expected 'participants' to be an array");
  }

  const items = participants.map(p => ({
    ...p,
    category : activity.category,
    activity
  }));

  // Retorna um único objeto com atributo `items`
  return {
    json: {
      items
    }
  };
} catch (e) {
  throw new Error("Failed to parse or process JSON:\n" + e.message + "\nRaw:\n" + raw);
}

const input = $json;

if (input.category !== "professional_training" || !input.activity) {
  return [{ json: { score: 0, message: "Not applicable" } }];
}

const activity = input.activity;
let score = 0;

// Base score for certificate
if (activity.certificate === true) {
  score += 50;
} else {
  score += 30;
}

// Bonus for long duration
if (activity.duration_hours && activity.duration_hours >= 8) {
  score += 10;
}

// Bonus for known platforms
const recognizedPlatforms = ["Coursera", "Udemy", "LinkedIn Learning", "AWS Training"];
if (activity.platform && recognizedPlatforms.includes(activity.platform)) {
  score += 10;
}

// Apply multiplier by type
const type = (input.type || "").toLowerCase();

let multiplier = 1.0;
if (type === "capitão") {
  multiplier = 1.2;
} else if (type === "governança") {
  multiplier = 0.8;
}

const finalScore = Math.round(score * multiplier);

return {
  json: {
    ...input,
    base_score: score,
    multiplier: multiplier,
    calculated_score: finalScore
  }
};

